.close-button {
    border-style: solid;
    position: relative;
    cursor: pointer;
    margin-left: 1.5rem;
}

.close-button::before,
.close-button::after {
    position: absolute;
    top: 50%;
    left: 50%;
    content: '';
    transform-origin: top left;
}

.close-button::before {
    transform: rotate(45deg) translate(-50%, -50%);
}

.close-button::after {
    transform: rotate(-45deg) translate(-50%, -50%);
}

